import React, { useState } from 'react';
import { View, Text, Button,StyleSheet,ScrollView,TextInput } from 'react-native';
import { createAccount } from '../api/authentication';
// import SignupForm from '../forms/SignupForms';
import InputSpinner from "react-native-input-spinner";
import SwitchSelector from "react-native-switch-selector";
import { widthPercentageToDP } from 'react-native-responsive-screen';
const SignupForm = ({ buttonText, onSubmit, children, onAuthentication ,navigation}) => {
  const [name, onChangeName] = useState('');
  const [password, onChangePassword] = useState('');
  const [confpassword, onChangeConfPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const[email,onChangeEmail]=useState('');
  const [state, setState] = useState({
        type:'',
        class:1
      })
  // const submit = () => {
  //   onSubmit(email, password)
  //     .then(async (res) => {
  //       await setToken(res.auth_token);
  //       onAuthentication();
  //     })
  //     .catch((res) => {
  //       if (res && res.error) {
  //         setErrorMessage(res.error);
  //       }

  //       setErrorMessage('Something went wrong.');
  //     });
  // };

  const submit=()=>{
    if (password == confpassword){
      console.log('Yes')
      const user={
        name:name,
        email:email,
        password:password,
        role:state.type==='S'?'Student':'Teacher',
        class:state.class+1
      }
      console.log(user)
    }
   else{
     console.log('No')
   }

  }
  return (
    <ScrollView contentContainerStyle={styles.container}>

      <TextInput
        placeholder='Enter your Name'
        style={styles.input}
        onChangeText={(text) => onChangeName(text)}
        value={name}
      />
      <TextInput
        placeholder='Enter your Password'
        style={styles.input}
        onChangeText={(text) => onChangePassword(text)}
        value={password}
        secureTextEntry
      />
       <TextInput
        placeholder='Confirm your Password'
        style={styles.input}
        onChangeText={(text) => onChangeConfPassword(text)}
        value={confpassword}
        secureTextEntry
      />
      <TextInput 
        label="Email"
        placeholder="Email"
        value={email}
        blurOnSubmit={false}
        maxLength={100}
        style={styles.input}
        onChangeText={(text) => onChangeEmail(text)}
      />
      <Text>{'\n'}</Text>
      <InputSpinner
    style={{maxWidth:widthPercentageToDP('10%'), marginLeft:-10}}
    max={12}
    min={1}
    step={1}
    colorMax={"#f04048"}
    colorMin={"#40c5f4"}
    value={state.class}
    onChange={(num) => {
        setState({class:num})
    }}
/>
      <Text>{"\n"}</Text>
         <SwitchSelector
  initial={0}
  onPress={value => setState({ type: value })}
  // textColor={colors.purple} //'#7a44cf'
  // selectedColor={colors.white}
  // buttonColor={colors.purple}
  // borderColor={colors.purple}
  style={{width: widthPercentageToDP('50%')}}
  buttonColor='#7a44cf'
  borderColor='#7a44cf'
  textColor='#7a44cf'
  options={[
    { label: "Teacher", value: "T" }, //images.feminino = require('./path_to/assets/img/feminino.png')
    { label: "Student", value: "S" } //images.masculino = require('./path_to/assets/img/masculino.png')
  ]}
/>
<Text>{'\n'}</Text>
 <Button title={buttonText} color='black' onPress={submit}/>
      {errorMessage ? <Text>{errorMessage}</Text> : null}
      {children}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  input: {
    paddingLeft:10,
    height: 40,
    width: 300,
    borderColor: 'gray',
    borderWidth: 1,
    marginTop: 20,
  },
});
const SignupScreen = ({ navigation }) => {
  return (
    <SignupForm buttonText="Sign Up"  color='black'>
      <Text>{"\n"}</Text>
      {/* <Button

        title='Sign Up'
        onPress={()=> {console.log(name)}}>
      </Button> */}
      {/* <Text>{"\n"}</Text> */}
      <Button style={{marginTop:10}}
        color="black"
        title="Back to first page"
        onPress={() => navigation.navigate('First')}
      />
    </SignupForm>
  );
};

export default SignupScreen;
