import React, { useState } from 'react';
import { View, Text, Button ,StyleSheet, TouchableOpacity} from 'react-native';
import { widthPercentageToDP } from 'react-native-responsive-screen';
import Icon from 'react-native-vector-icons/SimpleLineIcons';
import { getToken } from"./../api/token";
import jwt_decode from "jwt-decode";
import { LinearGradient } from "expo-linear-gradient";
const StudentDash = ({ navigation }) => {
  var token = navigation.getParam('data');
//   async () => {   
//     token =  await getToken();
//   }
console.log(token)
var decoded = jwt_decode(token);
console.log(decoded)

  return (
      <View>
    <Text>{'\n'}</Text>
    <Text>{'\n'}</Text>
  <Text style={styles.greeting}>Hi, {decoded.username}</Text>
  <Text>{'\n'}</Text>
  <Text style={{textAlign:'center', color:'#C0C0C0'}}>You currently have 0 points</Text>
    <Text>{'\n'}</Text>
    <Text>{'\n'}</Text>

    <TouchableOpacity style={styles.touchableopacity}>
            <LinearGradient
              colors={["#000000", "#434343"]}
              start={[0.1, 0.1]}
              style={styles.linearGradient}
            >
              <View style={styles.imgrow}>
                <Image
                  source={{
                    uri:
                      "https://img.icons8.com/color/48/000000/night-landscape.png",
                  }}
                  style={{
                    width: wp("20%"),
                    height: hp("20%"),
                    resizeMode: "center",
                    flexDirection: "row",
                    marginLeft: wp("5%"),
                  }}
                />
                <Text
                  style={{
                    alignSelf: "center",
                    fontWeight: "100",
                    fontFamily: "sans-serif",
                    color: "white",
                  }}
                >
                  Dark Mode
                </Text>
                <Text
                  style={{
                    position: "absolute",
                    right: wp("5%"),
                    top: wp("5%"),
                    fontWeight: "bold",
                    color: "white",
                  }}
                >
                  1200
                </Text>
              </View>
            </LinearGradient>
          </TouchableOpacity>
    </View>
  );
};
const styles = StyleSheet.create({
    greeting: {
      fontWeight:'bold',
      fontSize:widthPercentageToDP('6%'),
      fontFamily:'sans-serif',
      textAlign:'center'
    },
    content: {
        textAlign:'center',
        color:'#C0C0C0'
    }
})
export default StudentDash;
