import AsyncStorage from '@react-native-community/async-storage';

export const getToken = async function(token) {
  return new Promise(async (resolve, reject) => {
    try {
      const value =  AsyncStorage.getItem('@auth_token')
      resolve(value)
    } catch (e) {
      resolve(false)
    }
  // } catch (e) {
  //   return null;
  // }
})
};

