export const setToken = async function (token) {
  return new Promise(async (resolve, reject) => {
    try {
      AsyncStorage.setItem('@auth_token', token); 
      console.log("Token is set")
      resolve(true)
    } catch (e) {
      //important that we do not reject here
      resolve(false)
    }
  })
};
