        onChangeText={(text) => setState({name: text})}
        value={state.name || ''}
      />
      <TextInput
        placeholder='Enter your Password'
        style={styles.input}
        onChangeText={(text) => setState({Password: text})}
        value={state.Password}
        secureTextEntry
      />
      <TextInput 
        label="Address"
        placeholder="Add"
        value={state.Address}
        blurOnSubmit={false}
        maxLength={100}
        onChangeText={(text) => setState({Address: text})}
      />
       <br>
      </br>
      <Button title={buttonText} color='black' onPress={submit} />
      {errorMessage ? <Text>{errorMessage}</Text> : null}
      {children}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  input: {
    paddingLeft:10,
    height: 40,
    width: 300,
    borderColor: 'gray',
    borderWidth: 1,
    marginTop: 20,
  },
});

export default SignupForm;
