import React, { useState } from 'react';
import { View, Text, SafeAreaView ,StyleSheet, TouchableOpacity,FlatList, StatusBar} from 'react-native';
import { widthPercentageToDP } from 'react-native-responsive-screen';
import Icon from 'react-native-vector-icons/SimpleLineIcons';
import { getToken } from"./../api/token";
import jwt_decode from "jwt-decode";
import { LinearGradient } from "expo-linear-gradient";
const StudentDash = ({ navigation }) => {
  var token = navigation.getParam('data');
  const DATA = [
    {
      id: '1',
      title: 'First Assignment',
      color1:'#ED213A',
      color2:'#93291E'
    },
    {
      id: '2',
      title: 'Second Assignment',
      color1:'#FDC830',
      color2:'#F37335'
    },
    {
      id: '3',
      title: 'Third Assignment',
      color1:'#11998e',
      color2:'#38ef7d'
    },
  ];
  const Item = ({ title }) => (
    <LinearGradient
    colors={[Item.color1, Item.color2]}
    start={[0.1, 0.1]}
    style={styles.linearGradient}
  >
    <TouchableOpacity style={styles.item}>

      <Text style={styles.title}>{title}</Text>

    </TouchableOpacity>
    </LinearGradient>
  );


var decoded = jwt_decode(token);
const renderItem = ({ item }) => (
    <Item title={item.title} />
  );
  return (
      <View>
    <Text>{'\n'}</Text>
    <Text>{'\n'}</Text>
  <Text style={styles.greeting}>Hi, {decoded.username}</Text>
  <Text>{'\n'}</Text>
  <Text style={{textAlign:'center', color:'#C0C0C0'}}>You currently have 0 points</Text>
    <Text>{'\n'}</Text>
    <SafeAreaView style={styles.container}>
      <FlatList
        data={DATA}
        renderItem={renderItem}
        keyExtractor={item => item.id}
      />
    </SafeAreaView>
    </View>
  );
};
const styles = StyleSheet.create({
    greeting: {
      fontWeight:'bold',
      fontSize:widthPercentageToDP('6%'),
      fontFamily:'sans-serif',
      textAlign:'center'
    },
    content: {
        textAlign:'center',
        color:'#C0C0C0'
    },
    container: {
        flex: 1,
        marginTop: StatusBar.currentHeight || 0,
      },
      item: {
        width:widthPercentageToDP('80%'),
        backgroundColor: '#ffffff03',
        padding: 20,
        marginVertical: 8,
        marginHorizontal: 16,
        borderRadius:widthPercentageToDP('5%'),
        textColor:'white'
      },
      title: {
        fontSize: 32,
      },
      linearGradient: {
        width:widthPercentageToDP('80%'),
        marginTop:widthPercentageToDP('3%'),
        alignSelf:'center',
        borderRadius: widthPercentageToDP("5%"),

      },
})
export default StudentDash;
