import React from 'react'
import {
  SafeAreaView,
  StyleSheet,
  ScrollView,
  View,
  Text,
  StatusBar,
  Button,
  TextInput,
  TouchableOpacity,

} from 'react-native'
import * as yup from 'yup'
import { Formik } from 'formik'
import { widthPercentageToDP } from 'react-native-responsive-screen'
import {post} from './../api/fetch'
const LoginScreen = () => {
    const loginValidationSchema = yup.object().shape({
        email: yup
          .string()
          .email("Please enter valid email")
          .required('Email Address is Required'),
        password: yup
          .string()
          .min(8, ({ min }) => `Password must be at least ${min} characters`)
          .required('Password is required'),
      })
  return (
    <View style={styles.loginContainer}>
    <Formik
   validationSchema={loginValidationSchema}
   initialValues={{ email: '', password: '' }}
   onSubmit={values => {
    let formDat = new FormData();
    formDat.append('username',values.email)
    formDat.append('password',values.password)
    for (var value of formDat.values()) {
        console.log(value); 
     }
     console.log((
        {
            'username': values.email,
            'password':values.password
        }.username
     )
     )
    post('/login',{
        'username': values.email,
        'password':values.password
    }).then(async (res)=>{
        console.log(res)
      })
   }}
 >
   {({
              handleChange,
              handleBlur,
              handleSubmit,
              values,
              errors,
              touched,
              isValid,
            }) => (

     <>



       <TextInput
         name="email"
         placeholder="Email Address"
         style={styles.textInput}
         onChangeText={handleChange('email')}
         onBlur={handleBlur('email')}
         value={values.email}
         keyboardType="email-address"
       />
       {(errors.email && touched.email) &&
                    <Text style={styles.errorText}>{errors.email}</Text>
                  }
       <TextInput
         name="password"
         placeholder="Password"
         style={styles.textInput}
         onChangeText={handleChange('password')}
         onBlur={handleBlur('password')}
         value={values.password}
         secureTextEntry
       />
         {(errors.password && touched.password) &&
                    <Text style={styles.errorText}>{errors.password}</Text>
                  }
        <Text>{'\n'}</Text>

       {/* <Button  color="#9370DB" style={{borderRadius:widthPercentageToDP('30%')}}
         onPress={handleSubmit}
         title="LOGIN"
         disabled={!isValid}
       /> */}
       <TouchableOpacity onPress={handleSubmit} disabled={!isValid} style={{backgroundColor: '#9370DB',height: widthPercentageToDP('10%'),
        width: widthPercentageToDP('70%'),borderRadius:widthPercentageToDP('5%'),overflow:'hidden',alignSelf:'center' }}>
   <Text style={{textAlign:'center',color:'white',fontFamily:'sans-serif',marginTop:widthPercentageToDP('2%')}}>LOGIN</Text>
</TouchableOpacity>
     </>
   )}
 </Formik>
  </View>
  )
}

const styles = StyleSheet.create({

    loginContainer: {
      width: '90%',
      height:'50%',
      alignSelf: 'center',
      backgroundColor: '#fafafa',
      padding: widthPercentageToDP('5%'),
      elevation: 10,
      marginTop:widthPercentageToDP('30%'),
      borderRadius:widthPercentageToDP('30%')
    },
    textInput: {
        paddingLeft:10,
        height: widthPercentageToDP('10%'),
        width: widthPercentageToDP('70%'),
        alignSelf:'center',
        borderColor: '#4B0082',
        borderRadius: widthPercentageToDP('5%'),
        borderWidth: 1,
        marginTop: 20,
        backgroundColor: '#f6f6f6'
      },
    errorText: {
        fontSize: 10,
        color: 'red',
      },
  })
export default LoginScreen
